                "getNewRootFromL2(uint256 _l2Root, uint256 _l2BlockNumber)",
                _l2Root,_l2BlockNumber 
            ),
            _gasLimit,
            msg.sender
        );
    }

    function receiveGigaRoot(uint256 _gigaRoot) public {
        gigaRoot = _gigaRoot;
        sendGigaRoot(l2WarpToad);
